import React from 'react'
import './categories.styles.scss'

const App = () => {

  const categories = [
    {
      id: 1,
      title: 'Hats',

    },

    {
      id: 2,
      title: 'Jacket',

    },

    {
      id: 3,
      title: 'Sneakers',

    },

    {
      id: 4,
      title: 'Women',

    },

    {
      id: 5,
      title: 'Men',

    }


  ]



  return (
    <div className='categories-container' >

      {categories.map(({title, id}) => (
        <div key={id} className='category-container' >

        {/* <img src='' />*/}

        <div className='category-body-container' > 
          <h2>{title}</h2>
          <p>Shop Now</p>
         </div>

      </div>
      ) )}




    </div>
  )
}

export default App
